[h,IF(reliable==1), CODE:
	{
	[h:expendDaily=if(hit=="hit","Expended","Available")]
	};
	{
	[h:expendDaily="Expended"]
	};
]

[h, SWITCH(json.get(power,"usage")), CODE:
case "daily":	{
	[h:power=json.set(power,"expended",expendDaily)]
	[h:Powers=json.set(Powers, pname, power)]
	[h:macProps=if(expendDaily=="Expended", "color=gray;", "color=black;")]
	[h:setMacroProps(pname, macProps)]
	};

case "encounter":{
	[h:power=json.set(power,"expended", "Expended")]
	[h:Powers=json.set(Powers, pname, power)]
	[h:setMacroProps(pname, "color=gray;")]
	};

case "at-will": {};

case "recharge":{
    	[h:power=json.set(power, "expended", "Must recharge")]
	    [h:Powers=json.set(Powers,pname, power)]
	    [h:setMacroProps(pname, "color=gray;")]
    	};
]


[h,SWITCH(json.get(power, "action")):
case "immediate reaction": state.Immed_Reaction_Used=1;
case "immediate interrupt": state.Immed_Reaction_Used=2;
default: state.Immed_Reaction_Used=0;]

